import React from "react";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useDentiStates } from '../Context/Context'



  const Card = () => {
    const jsonData = {
      "cards": [
        {
          "id": 1,
          "title": "KIA RÍO",
          "description": "La gama de motores del Kia Rio está compuesta por tres propulsores de gasolina. El motor de acceso es un 1,2 atmosférico de cuatro cilindros que desarrolla 84",
          "image": "https://4.bp.blogspot.com/-DGjuQYSWfQM/WefI7vviaEI/AAAAAAAChZU/OFBqLS7AobMNiHOWkSagL8EJiS1rDRckwCLcBGAs/s1600/kia-rio-uruguay-2018-precios-autoblog%2B%25283%2529.jpg",
        
        },
        {
          "id": 2,
          "title": "CHEVROLET ONIX ",
          "description": "Motor 1.3L de 3 cilindros normalmente aspirado · 101 HP de potencia · 96 lb-pie de torque · Transmisión manual de 5 velocidades · Luces delanteras de halógeno",
          "image": "data:image/jpeg;base64,"
        },
        {
          "id": 3,
          "title": "SUZUKI CELERIO",
          "description": "El Suzuki Celerio sólo está disponible con un único motor, un 1.0 atmosférico de gasolina, de tres cilindros. Este motor desarrolla 68 CV y un par máximo de 90.",
          "image": "data:image/jpeg;base64,"
        }
        
      ]
    };
    
    const [randomCard, setRandomCard] = useState(null);
  
    useEffect(() => {
      // Generar un índice aleatorio
      const randomIndex = Math.floor(Math.random() * jsonData.cards.length);
  
      // Seleccionar la tarjeta aleatoria
      const selectedCard = jsonData.cards[randomIndex];
  
      // Actualizar el estado con la tarjeta aleatoria
      setRandomCard(selectedCard);
    }, []);
  
    return (
      <div>
        {randomCard && (
          <div key={randomCard.id} className="card">
            <img src={randomCard.image} alt={randomCard.title} />
            
            <div className="card-content">
            <h3>{randomCard.title}</h3>
            <a href="\Detalles.jsx">
            <button>Ver más</button>
            </a>
              <p>{randomCard.description}</p>
            </div>
          </div>
        )}
      </div>
    );
  };
  

    

